mapper.select:
SELECT 

FROM fa_reimburse_main a

WHERE a.id = #{id}

SELECT
	u.name as userName,o.name as officeName,
	FROM fa_reimburse_main a
JOIN sys_user u ON a.applicantId = u.id
JOIN sys_office o ON a.officeId = o.id


	AND a.status = #{status}


	AND a.procInsId = #{procInsId}




		ORDER BY ${page.orderBy}

SELECT 

FROM fa_reimburse_main a






		ORDER BY ${page.orderBy}

SELECT u.name as userName,o.name as officeName,
FROM fa_reimburse_main a JOIN sys_user u ON a.applicantId = u.id JOIN sys_office o ON a.officeId = o.id JOIN fa_reimburse_longdistance l on l.mainId=a.id JOIN fa_reimburse_hospitality h on h.mainId=a.id JOIN fa_reimburse_other oth on oth.mainId=a.id JOIN fa_reimburse_taxi ta on ta.mainId=a.id
AND a.id = #{id}
ORDER BY ${page.orderBy}

mapper.update:
UPDATE fa_reimburse_main SET 	

	procInstId = #{procInstId}


	officeId = #{officeId}


	applicantId = #{applicantId}


	status = #{status}


	applyDate = #{applyDate}


	beginDate = #{beginDate}


	endDate = #{endDate}


	remark = #{remark}


	totalAmount = #{totalAmount}


	updateDate = #{updateDate}


	managerFlag = #{managerFlag}


	FAFlag = #{FAFlag}


	managerComment = #{managerComment}


	FAComment = #{FAComment}

WHERE id = #{id}
