mapper.select:
SELECT
FROM fa_reimburse_main a
WHERE a.delFlag = 1 And a.id = #{id}

SELECT u.name as "applicant.name", o.name as "office.name",
FROM fa_reimburse_main a JOIN sys_user u ON a.applicantId = u.id JOIN sys_office o ON a.officeId = o.id where a.delFlag = 1
AND a.status = #{status}
AND a.procInstId = #{procInstId}
ORDER BY a.updateDate DESC

SELECT u.name as "applicant.name", o.name as "office.name",
FROM fa_reimburse_main a JOIN sys_user u ON a.applicantId = u.id JOIN sys_office o ON a.officeId = o.id where a.delFlag = 1
AND a.id = #{mainId}

mapper.update:
UPDATE fa_reimburse_main SET 	

	procInstId = #{procInstId}


	officeId = #{office.id}


	applicantId = #{applicant.id}


	,status = #{status}


	applyDate = #{applyDate}


	beginDate = #{beginDate}


	endDate = #{endDate}


	remark = #{remark}


	totalAmount = #{totalAmount}


	updateDate = #{updateDate}


	managerFlag = #{managerFlag}


	FAFlag = #{FAFlag}


	,managerComment = #{managerComment}


	,FAComment = #{FAComment}

WHERE id = #{id}

UPDATE fa_reimburse_main SET 	
	delFlag = 0
WHERE id = #{id}
